sp_header_footer.client_script:
api.controller = function ($scope, $rootScope, $location) {
	var c = this;
	$scope.isLoggedIn = c.data.isLoggedIn;
	if(c.data.isLoggedIn){
		$scope.username = c.data.log.name;
		$scope.avatarUser = c.data.user_log;
	}

	////////integração com Server/////////
	c.search = "";

  c.send = function (event) {
        if (event.keyCode === 13) { // 13 é o código da tecla "Enter"
         $rootScope.$broadcast('integration_header', { key: 'Enter', search: c.search });
					 $location.path('/integration').search('id=integration_ms_page&query=' + encodeURIComponent(c.search));
        }
    };
};

